putHistogram.resize(256);	targetHistogram.resize(256);	inputCDF.resize(256);	targetCDF.resize(256);	for (int grayScale=0; grayScale<256; grayScale++){	  inputHistogram[grayScale].resize(BytesPerPixel);	  targetHistogram[grayScale].resize(BytesPerPixel);	  inputCDF[grayScale].resize(BytesPerPixel);	  targetCDF[grayScale].resize(BytesPerPixel);	}	//these parameters are observed and taken from the skyline image	//the mactching gaussian distribution is obtained with these parameters	float sigma[3] = {30.25, 31.34, 35.34};	float mean[3] = {163.59, 70.22, 104.79};	int lowerLim[3] = {107, 31, 59};	int upperLim[3] = {224, 142, 160};	//initialize the inputHistogram to 0	for (int channels=0; channels<BytesPerPixel; channels++){	  for (int grayScale=0; grayScale<=255; grayScale++){	    inputHistogram[grayScale][channels] = 0;	  }	}		//populate the histogram	for (int channels=0; channels<BytesPerPixel; channels++){	  for (int row=0; row<Height; row++){	    for (int col=0; col<Width; col++){	      inputHistogram[Imagedata[row][col][channels]][channels]++ ;	    }	  }	}	for (int channels=0; channels<BytesPerPixel; channels++){	  for (int grayScale=0; grayScale<256; grayScale++){	    targetHistogram[grayScale][channels] = exp( -1 * pow(grayScale-mean[channels],2)/(2*pow(sigma[channels],2)))/(float)(sqrt(2*M_PI)*sigma[channels]);	  }	}		//Normalize the input histogram	for (int channels=0; channels<BytesPerPixel; channels++){	  for (int grayScale=0; grayScale<=255; grayScale++){	    inputHistogram[grayScale][channels] = inputHistogram[grayScale][channels]/(float)(Height*Width);	  }	}	//convert it to CDF	for (int channels=0; channels<BytesPerPixel; channels++){	  	  inputCDF[0][channels] = inputHistogram[0][channels];	  targetCDF[0][channels] = targetHistogram[0][channels];	  	  for (int grayScale=1; grayScale<=255; grayScale++){	   	    inputCDF[grayScale][channels] = inputHistogram[grayScale][channels]+inputCDF[grayScale-1][channels];	    targetCDF[grayScale][channels] = targetHistogram[grayScale][channels]+targetCDF[grayScale-1][channels];	  	  }	}	//compute the values for the output histogram	for (int channels=0; channels<BytesPerPixel; channels++){	  for (int row=0; row<Height; row++){	    for (int col=0; col<Width; col++){	      	      float inputCDFVal = inputCDF[ Imagedata[row][col][channels] ][channels];	      float closestMatch = 5000;	      float targetCDFGrayScale = 0;	      	      //find the closest matching point on the cdf (since this is a discrete distribution)	      for (int i=lowerLim[channels]; i<=upperLim[channels]; i++){		float error = abs(inputCDFVal - targetCDF[i][channels]);		if (error<closestMatch){		  closestMatch = error;		  targetCDFGrayScale = i;		}	      }	      	      tempImage[row][col][channels] = targetCDFGrayScale;	    	    }	  }	}		//Renormalize the values to fit in the 8 bit range	for (int channels=0; channels<BytesPerPixel; channels++){	  for (int row=0; row<Height; row++){	    for (int col=0; col<Width; col++){	      	      newImage[row][col][channels] = (unsigned char)(tempImage[row][col][channels]);	    }	  }	}		// Write image data (filename specified by second argument) from image data matrix	if (!(file=fopen(argv[2],"wb"))) {		cout << "Cannot open file: " << argv[2] << endl;		exit(1);	}	fwrite(newImage, sizeof(unsigned char), Height*Width*BytesPerPixel, file);	fclose(file);	return 0;}