// Homework 1// This code performs histogram equalization// Question 2.a.method A and 2.b.method A// This code takes 6 arguments in the following format// program_name input_image.raw output_methodA.raw [BytesPerPixel = 1] [Width = 256] [Height = 256]// //// Name: Ekraam Sabir // USC ID: 1428-2892-90// email: esabir@usc.edu// #include <stdio.h>#include <iostream>#include <stdlib.h>#include <math.h>using namespace std;int main(int argc, char *argv[]){	// Define file pointer and variables	FILE *file;	int BytesPerPixel;	int Height = 256;	int Width = 256;		// Check for proper syntax	if (argc < 5){		cout << "Syntax Error - Incorrect Parameter Usage:" << endl;		cout << "This program performs Histogram equalization" << endl;		cout << "You must have 6 arguments in the following format" << endl;		cout << "program_name input_image.raw output_methodA.raw [BytesPerPixel = 1] [Width = 256] [Height = 256]" << endl;		return 0;	}	//Get the command line parameters in the appropriate variables	else{	  BytesPerPixel = atoi(argv[3]);	  Width = atoi(argv[4]);	  Height = atoi(argv[5]);	}			// Allocate image data array	unsigned char Imagedata[Height][Width][BytesPerPixel];	// Read image (filename specified by first argument) into image data matrix	if (!(file=fopen(argv[1],"rb"))) {		cout << "Cannot open file: " << argv[1] <<endl;		exit(1);	}	fread(Imagedata, sizeof(unsigned char), Height*Width*BytesPerPixel, file);	fclose(file);	///////////////////////// INSERT YOUR PROCESSING CODE HERE /////////////////////////		//create a blank new image array	unsigned char newImage[Height][Width][BytesPerPixel];	float inputHistogram = [256][BytesPerPixel];	//initialize the inputHistogram to 0	for (int channels=1; channels<=BytesPerPixel; channels++){	  for (int grayScale=0; grayScale<=255; grayScale++){	      inputHistogram[grayScale][channels] = 0;	  }	}	//populate the histogram	for (int channels=1; channels<=BytesPerPixel; channels++){	  for (int row=0; row<Height; row++){	    for (int col=0; col<Width; col++){	      inputHistogram[Imagedata[row][col][channels]][channels]++ ;	    }	  }	}	//normalize the histogram	for (int channels=1; channels<=BytesPerPixel; channels++){	  for (int grayScale=0; grayScale<=255; grayScale++){	    inputHistogram[grayScale][channels] = inputHistogram[grayScale][channels]/(Height*Width);	  }	}	//calculate the CDF		for (int row=0; row<Height; row++){	  for (int col=0; col<Width; col++){	    newImage[row][col][0] = newImage_C[row][col];	    newImage[row][col][1] = newImage_M[row][col];	    newImage[row][col][2] = newImage_Y[row][col];	  }	}	// Write image data (filename specified by second argument) from image data matrix	if (!(file=fopen(argv[2],"wb"))) {		cout << "Cannot open file: " << argv[2] << endl;		exit(1);	}	fwrite(newImage, sizeof(unsigned char), Height*Width*BytesPerPixel, file);	fclose(file);	if (!(file=fopen(argv[3],"wb"))) {		cout << "Cannot open file: " << argv[3] << endl;		exit(1);	}	fwrite(newImage_C, sizeof(unsigned char), Height*Width, file);	fclose(file);	if (!(file=fopen(argv[4],"wb"))) {		cout << "Cannot open file: " << argv[4] << endl;		exit(1);	}	fwrite(newImage_M, sizeof(unsigned char), Height*Width, file);	fclose(file);	if (!(file=fopen(argv[5],"wb"))) {		cout << "Cannot open file: " << argv[5] << endl;		exit(1);	}	fwrite(newImage_Y, sizeof(unsigned char), Height*Width, file);	fclose(file);	return 0;}